contracts/Aggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

contract Aggregator {
    struct Battery {
        address owner;
        uint capacity; // in kWh
        uint SoC; // State of Charge in percentage
        bool isRegistered;
    }

    mapping(address => Battery) public batteries;
    address[] public batteryAddresses;
    address public aggregatorAdmin;
    uint public commissionRate; // Commission rate in percentage, e.g., 5 for 5%

    modifier batteryIsNotRegistered() {
        require(
            !batteries[msg.sender].isRegistered,
            "Battery already registered"
        );
        _;
    }
    constructor(address _aggregatorAdmin, uint _commissionRate) {
        aggregatorAdmin = _aggregatorAdmin;
        commissionRate = _commissionRate;
    }

    function registerBattery(
        address _owner,
        uint _capacity,
        uint _SoC,
        bool isRegistered
    ) external batteryIsNotRegistered {
        batteries[_owner] = Battery(_owner, _capacity, _SoC, isRegistered);
        batteryAddresses.push(_owner);
    }

    function updateBatterySoCAfterSale(
        address _owner,
        uint _amountSold,
        bool _isPositiveReserve
    ) external {
        Battery storage battery = batteries[_owner];
        require(battery.owner == _owner, "Battery not found");
        if (_isPositiveReserve) {
            uint newSoc = uint(
                battery.SoC - ((_amountSold * 100) / battery.capacity)
            ); // calculate new SoC
            battery.SoC = newSoc; // update the SoC
        } else {
            uint newSoc = uint(
                battery.SoC + ((_amountSold * 100) / battery.capacity)
            ); // calculate new SoC
            if (newSoc > 100) battery.SoC = 100;
            else battery.SoC = newSoc;
        }
    }

    // Funzione per ottenere il SoC di una batteria
    function getBatterySoC(address _batteryOwner) public view returns (uint) {
        require(
            batteries[_batteryOwner].owner != address(0),
            "Battery does not exist"
        );
        return batteries[_batteryOwner].SoC;
    }
}

contracts/Market.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Aggregator.sol";

contract Market {
    uint public marketOpenTime;
    uint public marketCloseTime;
    uint public resultsAnnouncementTime;
    address public marketAdmin;

    event MarketTimesUpdated(uint openTime, uint closeTime, uint resultsTime);
    event BidPlaced(
        address indexed bidder,
        address batteryOwner,
        uint amount,
        uint price
    );
    event PurchaseConfirmed(
        address indexed buyer,
        address indexed seller,
        uint bidId
    );
    event BidAccepted(uint bidId, address indexed buyer);
    event CommissionPaid(address indexed aggregator, uint amount);
    event PaymentToBatteryOwnerRecorded(
        uint bidId,
        address indexed batteryOwner,
        address indexed buyer,
        uint amount
    );
    event PaymentToAggregatorOwnerRecorded(
        uint bidId,
        address indexed aggregator,
        address indexed buyer,
        uint commission
    );

    struct Bid {
        address bidder;
        address batteryOwner;
        uint amount; // in kWh
        uint price; // in wei per kWh
        bool isSelected;
        address acceptedBy;
    }

    mapping(uint => Bid) public bids;
    uint public bidCount;
    mapping(address => address) public aggregators; // maps batteries owners addresses to aggregator addresses

    modifier onlyMarketAdmin() {
        require(
            msg.sender == marketAdmin,
            "Only the Market Admin can perform this action"
        );
        _;
    }

    modifier onlyDuringMarketOpen() {
        require(
            block.timestamp >= marketOpenTime &&
                block.timestamp <= marketCloseTime,
            "Market is not open"
        );
        _;
    }

    modifier onlyDuringAcceptancePeriod() {
        require(
            block.timestamp > marketCloseTime &&
                block.timestamp < resultsAnnouncementTime,
            "Acceptance period is not open"
        );
        _;
    }

    modifier onlyDuringResultsAnnouncement() {
        require(
            block.timestamp >= resultsAnnouncementTime,
            "Results announcement period is not open"
        );
        _;
    }

    constructor(address _marketAdmin) {
        marketAdmin = _marketAdmin;
    }

    function setAggregator(address _owner, address _aggregator) public {
        aggregators[_owner] = _aggregator;
    }

    function setMarketTimes(
        uint _openTime,
        uint _closeTime,
        uint _resultsTime
    ) public onlyMarketAdmin {
        marketOpenTime = _openTime;
        marketCloseTime = _closeTime;
        resultsAnnouncementTime = _resultsTime;
        emit MarketTimesUpdated(_openTime, _closeTime, _resultsTime);
    }

    function placeBid(
        address _bidder,
        address _batteryOwner,
        uint _amount,
        uint _price
    ) public onlyDuringMarketOpen {
        require(_price > 0, "price must be greater than 0");

        address aggregatorAddress = aggregators[_batteryOwner];
        require(
            aggregatorAddress != address(0),
            "Aggregator not found for battery owner"
        );

        Aggregator aggregator = Aggregator(aggregatorAddress);
        // Verify that the battery's SoC is greater than 50%
        uint batterySoC = aggregator.getBatterySoC(_batteryOwner);
        require(batterySoC >= 50, "Battery SoC must be >= 50 to place a bid");

        bids[bidCount] = Bid(
            _bidder,
            _batteryOwner,
            _amount,
            _price,
            false,
            address(0)
        );
        emit BidPlaced(_bidder, _batteryOwner, _amount, _price);
        bidCount++;
    }

    function acceptBid(uint _bidId) public onlyDuringAcceptancePeriod {
        require(bids[_bidId].bidder != address(0), "Bid does not exist");
        require(!bids[_bidId].isSelected, "Bid already selected");
        bids[_bidId].isSelected = true;
        bids[_bidId].acceptedBy = msg.sender;
        emit BidAccepted(_bidId, msg.sender);
    }

    function purchaseEnergy(
        uint _bidId
    ) public payable onlyDuringResultsAnnouncement {
        require(bids[_bidId].isSelected, "Bid not accepted");
        require(
            bids[_bidId].acceptedBy == msg.sender,
            "Only accepted buyer can purchase"
        );
        require(
            msg.value == bids[_bidId].amount * bids[_bidId].price,
            "Insufficient funds"
        );

        address owner = bids[_bidId].bidder;
        address batteryOwner = bids[_bidId].batteryOwner;

        address aggregatorAddress = aggregators[bids[_bidId].batteryOwner];
        Aggregator aggregator = Aggregator(aggregatorAddress);

        // commission calculation
        uint commission = (msg.value * aggregator.commissionRate()) / 100;

        payable(owner).transfer(commission);
        emit PaymentToAggregatorOwnerRecorded(
            _bidId,
            owner,
            msg.sender,
            commission
        ); // Log payment to aggregator owner

        payable(batteryOwner).transfer(msg.value - commission);
        emit PaymentToBatteryOwnerRecorded(
            _bidId,
            batteryOwner,
            msg.sender,
            msg.value - commission
        ); // Log payment to battery owner

        // Update the battery's SoC after the sale
        aggregator.updateBatterySoCAfterSale(
            bids[_bidId].batteryOwner,
            bids[_bidId].amount,
            true
        );
    }
}

contracts/TSO.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Aggregator.sol";

contract TSO {
    struct Bid {
        address bidder; // Address of the aggregator that placed the bid
        address batteryOwner;
        uint amount; // in kWh
        uint price; // in wei per kWh
        uint bidIndex;
        bool isSelected;
    }

    Aggregator public aggregator;
    mapping(address => address) public aggregators; // maps batteries owners addresses to aggregator addresses
    mapping(uint => Bid) public bids;

    bool public marketOpen;
    uint public requiredEnergy; // energy need from TSO
    bool public isPositiveReserve; // True for positive reserve, False for negative reserve
    address public tsoAdmin;
    address public aggregatorOwner;

    //uint public nextBidIndex; // Indice della prossima bid da selezionare
    //uint public nextPaymentIndex; // Indice del prossimo pagamento da elaborare
    uint public totalSelectedEnergy; // Energia totale selezionata

    event MarketOpened(uint requiredEnergy, bool isPositiveReserve);
    event BidPlaced(
        address indexed bidder,
        address batteryOwner,
        uint amount,
        uint price,
        uint bidIndex
    );
    event MarketClosed();
    event BidSelected(
        address indexed batteryOwner,
        uint amount,
        uint price,
        uint bidIndex
    );
    event PaymentToAggregatorOwnerRecorded(
        address indexed bidder,
        uint commission
    );
    event PaymentToBatteryOwnerRecorded(
        address indexed batteryOwner,
        uint payment
    );

    modifier onlyTsoAdmin() {
        require(
            msg.sender == tsoAdmin,
            "Only the TSO Admin can perform this action"
        );
        _;
    }

    modifier onlyAggregatorOwner() {
        require(
            msg.sender == aggregator.aggregatorAdmin(),
            "Only the Aggregator Owner can perform this action"
        );
        _;
    }

    modifier onlyWhenMarketOpen() {
        require(marketOpen, "Market is not open");
        _;
    }

    constructor(address _tsoAdmin) {
        tsoAdmin = _tsoAdmin;
    }

    function openMarket(
        uint _requiredEnergy,
        bool _isPositiveReserve
    ) external onlyTsoAdmin {
        requiredEnergy = _requiredEnergy;
        isPositiveReserve = _isPositiveReserve;
        marketOpen = true;
        emit MarketOpened(requiredEnergy, isPositiveReserve);
    }

    function placeBid(
        address _bidder,
        address _batteryOwner,
        uint _amountInKWh, // Volume in kWh
        uint _pricePerMWh, // Price in EUR/MWh
        uint _bidIndex
    ) external onlyWhenMarketOpen {
        uint batterySoC = aggregator.getBatterySoC(_batteryOwner);

        if (isPositiveReserve) {
            require(batterySoC >= 40, "Insufficient SoC to join the market");
        } else {
            require(batterySoC < 100, "Battery is full");
        }

        // Check if price is positive
        require(_pricePerMWh > 0, "Price must be greater than 0");

        address aggregatorAddress = aggregators[_batteryOwner];
        require(
            aggregatorAddress != address(0),
            "Aggregator not found for battery owner"
        );

        // Calculating the price per kWh
        //uint _pricePerKWh = (_pricePerMWh * _amountInKWh) / 1000;

        bids[_bidIndex] = Bid(
            _bidder,
            _batteryOwner,
            _amountInKWh,
            _pricePerMWh,
            _bidIndex,
            false
        );

        emit BidPlaced(
            _bidder,
            _batteryOwner,
            _amountInKWh,
            _pricePerMWh,
            _bidIndex
        );
    }

    function closeMarket() external {
        require(marketOpen, "Market is already closed");
        marketOpen = false;
        emit MarketClosed();
    }

    function selectNextBid(uint nextBidIndex) external onlyTsoAdmin {
        require(!marketOpen, "Market is still open");
        //require(nextBidIndex < bidCount, "All bids processed");

        uint energySelected = totalSelectedEnergy; // Track total selected energy
        if (energySelected < requiredEnergy) {
            bids[nextBidIndex].isSelected = true;
            energySelected += bids[nextBidIndex].amount;
            totalSelectedEnergy = energySelected; // Update the total selected energy
            emit BidSelected(
                bids[nextBidIndex].batteryOwner,
                bids[nextBidIndex].amount,
                bids[nextBidIndex].price,
                bids[nextBidIndex].bidIndex
            );
        }
        //nextBidIndex++; // Move to the next bid
    }

    function processNextPayment(uint nextPaymentIndex) external onlyTsoAdmin {
        require(!marketOpen, "Market is still open");
        //require(nextPaymentIndex < bidCount, "All payments processed");

        Bid storage bid = bids[nextPaymentIndex];
        if (bid.isSelected) {
            uint payment = bid.amount * bid.price;
            uint commission = (payment * aggregator.commissionRate()) / 100;

            // Pay battery owner
            payable(bid.batteryOwner).transfer(payment - commission);
            emit PaymentToBatteryOwnerRecorded(
                bid.batteryOwner,
                payment - commission
            );

            // Pay aggregator
            payable(address(aggregator)).transfer(commission);
            emit PaymentToAggregatorOwnerRecorded(bid.bidder, commission);

            // Update battery SoC
            aggregator.updateBatterySoCAfterSale(
                bid.batteryOwner,
                bid.amount,
                isPositiveReserve
            );
        }
    }

    function getBidIndex(uint _index) public view returns (uint) {
        return bids[_index].bidIndex;
    }

    function getBatteryOwner(uint _index) public view returns (address) {
        return bids[_index].batteryOwner;
    }

    function getBidder(uint _index) public view returns (address) {
        return bids[_index].bidder;
    }
}

